      red: { color: 'rgba(255, 0, 0, 0.8)', width: 1.5 },
      green: { color: 'rgba(0, 255, 0, 0.8)', width: 1.5 },
      blue: { color: 'rgba(0, 0, 255, 0.8)', width: 1.5 },
      intensity: { color: 'rgba(255, 255, 255, 0.8)', width: 2 }
