          // Apply flip if enabled
          if (flipXAxis) {
            xPos = 1 - xPos;
          }
          
          // Convert to pixel coordinates
          const x = xPos * width;
          const y = height - (peak.value / maxValue) * height;
          
          // Check if peak is near the top of the graph (below a minimum distance from top)
