        setTimeout(() => setSaveMessage && setSaveMessage(''), 3000);
      }
      
    } catch (error) {
      console.error('Failed to export graph data:', error);
      if (props.onMessage) {
        props.onMessage('Error exporting data');
      } else {
        setSaveMessage && setSaveMessage('Error exporting data');
        setTimeout(() => setSaveMessage && setSaveMessage(''), 3000);
