              }
              
              if (typeof settings.calibration.flipXAxis === 'boolean') {
                setFlipXAxis(settings.calibration.flipXAxis);
              }
              
              if (typeof settings.calibration.showCalibration === 'boolean') {
                setShowCalibration(settings.calibration.showCalibration);
              }
            }
            
            // Apply display settings
            if (settings.display) {
              if (settings.display.channels) {
                setDisplayChannels(settings.display.channels);
              }
              
              if (settings.display.graphSize) {
                setGraphSize(settings.display.graphSize);
                if (onResize) {
                  onResize(settings.display.graphSize);
                }
              }
            }
            
            // Apply accumulation settings
            if (settings.accumulation) {
              if (typeof settings.accumulation.frameAccumCount === 'number') {
                setFrameAccumCount(settings.accumulation.frameAccumCount);
                if (onFrameAccumCountChange) {
                  onFrameAccumCountChange(settings.accumulation.frameAccumCount);
                }
              }
              
              if (typeof settings.accumulation.isAccumulating === 'boolean') {
                setIsAccumulating(settings.accumulation.isAccumulating);
                if (onAccumulationToggle) {
                  onAccumulationToggle(settings.accumulation.isAccumulating);
                }
              }
            }
            
            // Apply peak marker settings
            if (settings.peaks) {
              if (typeof settings.peaks.showPeakMarkers === 'boolean') {
                setShowPeakMarkers(settings.peaks.showPeakMarkers);
              }
              
              if (typeof settings.peaks.peakCount === 'number') {
                setPeakCount(settings.peaks.peakCount);
              }
            }
            
            // Apply export settings
            if (settings.export) {
              if (settings.export.filePrefix) {
                setFilePrefix(settings.export.filePrefix);
              }
              
              if (typeof settings.export.includeDateInFilename === 'boolean') {
                setIncludeDateInFilename(settings.export.includeDateInFilename);
              }
            }
            
            // Reset accumulated data when loading settings
            setAccumulatedFrames([]);
            setAccumulatedData(null);
            
            // Show success message
            setSaveMessage('Settings loaded successfully');
            setTimeout(() => setSaveMessage(''), 3000);
          } catch (error) {
            console.error('Failed to parse settings file:', error);
            setSaveMessage('Error loading settings: Invalid file format');
            setTimeout(() => setSaveMessage(''), 3000);
          }
        };
        
        reader.onerror = () => {
          setSaveMessage('Error reading settings file');
          setTimeout(() => setSaveMessage(''), 3000);
        };
        
        // Read the file as text
        reader.readAsText(file);
      };
      
      // Trigger file selection dialog
      document.body.appendChild(fileInput);
      fileInput.click();
      
      // Clean up
      setTimeout(() => {
        document.body.removeChild(fileInput);
      }, 100);
    } catch (error) {
      console.error('Failed to load settings:', error);
      setSaveMessage('Error loading settings');
      setTimeout(() => setSaveMessage(''), 3000);
    }
  };
  
  // Handle peak count change
  const handlePeakCountChange = (e) => {
    const count = parseInt(e.target.value);
    if (!isNaN(count) && count >= 0 && count <= 10) {
      setPeakCount(count);
    }
  };
  
  // Toggle peak markers
  const togglePeakMarkers = () => {
    setShowPeakMarkers(!showPeakMarkers);
  };
  
  const styles = {
    container: {
      backgroundColor: 'rgba(0, 0, 0, 0.2)',
      padding: '10px',
      borderRadius: '4px',
      position: 'relative',
      border: isResizing ? '1px dashed #4CAF50' : 'none',
    },
    title: {
      margin: '0 0 10px 0',
      fontSize: '14px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    controls: {
      display: 'flex',
      gap: '5px',
      alignItems: 'center',
    },
    frameCountControl: {
      display: 'flex',
      alignItems: 'center',
      marginBottom: '10px',
      gap: '8px'
    },
    frameCountLabel: {
      fontSize: '12px',
      color: 'rgba(255, 255, 255, 0.8)'
    },
    frameCountInput: {
      width: '50px',
      padding: '2px 4px',
      backgroundColor: 'rgba(30, 30, 30, 0.8)',
      color: 'white',
      border: '1px solid rgba(80, 80, 80, 0.5)',
      borderRadius: '3px',
      fontSize: '12px'
    },
    calibrationContainer: {
      marginBottom: '10px',
      padding: '8px',
      backgroundColor: 'rgba(0, 0, 0, 0.3)',
      borderRadius: '4px'
    },
    calibrationHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '8px'
    },
    calibrationTitle: {
      fontSize: '12px',
      color: 'rgba(255, 255, 255, 0.9)',
      margin: 0
    },
    calibrationCheckbox: {
      marginLeft: '8px'
    },
    calibrationPoints: {
      display: 'flex',
      flexDirection: 'column',
      gap: '6px'
    },
    calibrationPoint: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      gap: '6px'
    },
    calibrationLabel: {
      fontSize: '11px',
      color: 'rgba(255, 255, 255, 0.7)',
      width: '40px'
    },
    calibrationInput: {
      width: '60px',
      padding: '2px 4px',
      backgroundColor: 'rgba(30, 30, 30, 0.8)',
      color: 'white',
      border: '1px solid rgba(80, 80, 80, 0.5)',
      borderRadius: '3px',
      fontSize: '11px'
    },
    graphView: {
      width: graphSize.width,
      height: `${graphSize.height}px`,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      position: 'relative',
      overflow: 'hidden'
    },
    canvas: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%'
    },
    resizeHandle: {
      position: 'absolute',
      bottom: '0',
      right: '0',
      width: '20px',
      height: '20px',
      cursor: 'ns-resize', // Only allow vertical resizing
      zIndex: 100,
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: 'rgba(60, 60, 60, 0.8)',
      borderTop: '1px solid rgba(180, 180, 180, 0.7)',
      borderLeft: '1px solid rgba(180, 180, 180, 0.7)',
      borderTopLeftRadius: '4px',
    },
    resizeInfo: {
      position: 'absolute',
      right: '25px',
      bottom: '25px',
      padding: '2px 6px',
      background: 'rgba(0, 0, 0, 0.7)',
      color: 'white',
      borderRadius: '3px',
      fontSize: '11px',
      zIndex: 100
    },
    channelToggles: {
      display: 'flex',
      gap: '5px'
    },
    channelButton: {
      fontSize: '10px',
      padding: '2px 5px',
      borderRadius: '3px',
      cursor: 'pointer',
      border: '1px solid rgba(255, 255, 255, 0.3)',
      backgroundColor: 'rgba(30, 30, 30, 0.7)'
    },
    noData: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      height: '100%',
      color: 'rgba(255, 255, 255, 0.5)',
      fontSize: '12px'
    }
  };
  
  // Return the component JSX
  return (
    <div style={styles.container}>
      <div style={styles.title}>
        <h3 style={{ margin: 0 }}>Intensity Graph</h3>
        <div style={styles.channelToggles}>
          <button 
            style={{
              ...styles.channelButton,
              color: displayChannels.red ? 'rgb(255, 100, 100)' : 'rgba(255, 100, 100, 0.4)',
              borderColor: displayChannels.red ? 'rgb(255, 100, 100)' : 'rgba(255, 100, 100, 0.2)'
            }}
            onClick={() => toggleChannel('red')}
          >
            R
          </button>
          <button 
            style={{
              ...styles.channelButton,
              color: displayChannels.green ? 'rgb(100, 255, 100)' : 'rgba(100, 255, 100, 0.4)',
              borderColor: displayChannels.green ? 'rgb(100, 255, 100)' : 'rgba(100, 255, 100, 0.2)'
            }}
            onClick={() => toggleChannel('green')}
          >
            G
          </button>
          <button 
            style={{
              ...styles.channelButton,
              color: displayChannels.blue ? 'rgb(100, 100, 255)' : 'rgba(100, 100, 255, 0.4)',
              borderColor: displayChannels.blue ? 'rgb(100, 100, 255)' : 'rgba(100, 100, 255, 0.2)'
            }}
            onClick={() => toggleChannel('blue')}
          >
            B
          </button>
          <button 
            style={{
              ...styles.channelButton,
              color: displayChannels.intensity ? 'rgb(220, 220, 220)' : 'rgba(220, 220, 220, 0.4)',
              borderColor: displayChannels.intensity ? 'rgb(220, 220, 220)' : 'rgba(220, 220, 220, 0.2)'
            }}
            onClick={() => toggleChannel('intensity')}
          >
            I
          </button>
          <button 
            style={{
              ...styles.channelButton,
              color: 'rgb(255, 255, 100)',
              borderColor: showCalibration ? 'rgb(255, 255, 100)' : 'rgba(255, 255, 100, 0.2)',
              backgroundColor: showCalibration ? 'rgba(60, 60, 0, 0.6)' : 'rgba(30, 30, 30, 0.7)'
            }}
            onClick={toggleCalibration}
            title="Toggle calibration controls"
          >
            Cal
          </button>
        </div>
      </div>
      
      <div style={styles.frameCountControl}>
        <div style={styles.frameCountLabel}>Frame accumulation:</div>
        <input 
          type="number" 
          min="1" 
          max="2000"
          value={frameAccumCount}
          onChange={handleFrameCountChange}
          style={styles.frameCountInput}
        />
        <button 
          style={{
            ...buttonVariants.smallIconButton,
            backgroundColor: isAccumulating ? 'rgba(255, 165, 0, 0.7)' : undefined
          }}
          onClick={toggleAccumulation}
          title={isAccumulating ? "Switch to live data" : "Switch to frame accumulation"}
        >
          {isAccumulating ? "Accumulating" : "Live"}
        </button>

        {/*button that pauses graph*/}
        <button 
          style={{
            ...buttonVariants.smallIconButton,
            backgroundColor: isPaused ? 'rgba(220, 53, 69, 0.7)' : undefined
          }}
          onClick={togglePause}
          title={isPaused ? "Resume graph updates" : "Pause graph updates"}
        >
          {isPaused ? "⏸️ Paused" : "▶️ Running"}
        </button>
        
        {/* Peak markers control */}
        <div style={{ 
          display: 'flex', 
          alignItems: 'center', 
          gap: '5px',
          marginLeft: '20px',
          borderLeft: '1px solid rgba(255, 255, 255, 0.2)',
          paddingLeft: '10px'
        }}>
          <div style={styles.frameCountLabel}>Peak markers:</div>
          <input 
            type="number" 
            min="0" 
            max="10"
            value={peakCount}
            onChange={handlePeakCountChange}
            style={styles.frameCountInput}
            title="Number of peak markers to display"
          />
          <button 
            style={{
              ...buttonVariants.smallIconButton,
              backgroundColor: showPeakMarkers ? 'rgba(120, 120, 220, 0.7)' : undefined
            }}
            onClick={togglePeakMarkers}
            title={showPeakMarkers ? "Hide peak markers" : "Show peak markers"}
          >
            {showPeakMarkers ? "Peaks On" : "Peaks Off"}
          </button>
        </div>
        
        {/* Add export controls */}
        <div style={{ display: 'flex', marginLeft: 'auto', alignItems: 'center', gap: '5px' }}>
          <input
            type="text"
            value={filePrefix}
            onChange={(e) => setFilePrefix(e.target.value)}
            placeholder="File prefix"
            style={{
              ...styles.frameCountInput,
              width: '100px'
            }}
            title="Prefix for the exported data filename"
          />
          <label style={{ 
            fontSize: '11px', 
            color: 'rgba(255, 255, 255, 0.7)', 
            display: 'flex', 
            alignItems: 'center',
            gap: '3px',
            marginRight: '5px'
          }}>
            <input 
              type="checkbox" 
              checked={includeDateInFilename}
              onChange={(e) => setIncludeDateInFilename(e.target.checked)}
              style={{ margin: 0 }}
            />
            Date
          </label>
          <button
            style={{
              ...buttonVariants.smallSecondary,
              backgroundColor: 'rgba(20, 120, 220, 0.7)'
            }}
            onClick={exportGraphData}
            title="Export graph data as text file"
            disabled={!graphData && !accumulatedData}
          >
            💾 Export Data
          </button>

          {/* Settings save/load buttons */}
          <button
            style={{
              ...buttonVariants.smallSecondary,
              backgroundColor: 'rgba(60, 160, 60, 0.7)',
              marginLeft: '10px'
            }}
            onClick={exportSettings}
            title="Save current settings to file"
          >
            💾 Save Settings
          </button>
          <button
            style={{
              ...buttonVariants.smallSecondary,
              backgroundColor: 'rgba(160, 120, 40, 0.7)'
            }}
            onClick={loadSettings}
            title="Load settings from file"
          >
            📂 Load Settings
          </button>
          
          {saveMessage && (
            <span style={{
              fontSize: '11px',
              backgroundColor: 'rgba(0, 0, 0, 0.6)',
              padding: '2px 6px',
              borderRadius: '3px',
              marginLeft: '5px',
              color: 'rgba(255, 255, 255, 0.9)'
            }}>
              {saveMessage}
            </span>
          )}
        </div>
      </div>
      
      {showCalibration && (
        <div style={styles.calibrationContainer}>
          <div style={styles.calibrationHeader}>
            <h4 style={styles.calibrationTitle}>Wavelength Calibration</h4>
            <div>
              <label style={{ fontSize: '11px', color: 'rgba(255, 255, 255, 0.7)' }}>
                <input 
                  type="checkbox" 
                  checked={useCalibration}
                  onChange={toggleUseCalibration}
                  style={styles.calibrationCheckbox}
                />
                Use Calibration
              </label>
            </div>
          </div>
          
          <div style={styles.calibrationPoints}>
            {calibrationPoints.map((point, index) => (
              <div key={index} style={styles.calibrationPoint}>
                <div style={styles.calibrationLabel}>Point {index+1}:</div>
                <div>
                  <span style={{ fontSize: '11px', color: 'rgba(255, 255, 255, 0.7)', marginRight: '4px' }}>Pos:</span>
                  <input 
                    type="number" 
                    min="0" 
                    max="1" 
                    step="0.0001"
                    value={point.position}
                    onChange={(e) => updateCalibrationPoint(index, 'position', e.target.value)}
                    style={styles.calibrationInput}
                  />
                </div>
                <div>
                  <span style={{ fontSize: '11px', color: 'rgba(255, 255, 255, 0.7)', marginRight: '4px' }}>λ:</span>
                  <input 
                    type="number" 
                    min="200" 
                    max="1200" 
                    step="0.01"
                    value={point.wavelength}
                    onChange={(e) => updateCalibrationPoint(index, 'wavelength', e.target.value)}
                    style={styles.calibrationInput}
                  />
                  <span style={{ fontSize: '11px', color: 'rgba(255, 255, 255, 0.7)', marginLeft: '2px' }}>nm</span>
                </div>
              </div>
            ))}
            
            <div style={{ marginTop: '8px', display: 'flex', alignItems: 'center' }}>
              <label style={{ fontSize: '11px', color: 'rgba(255, 255, 255, 0.7)', display: 'flex', alignItems: 'center' }}>
                <input 
                  type="checkbox" 
                  checked={flipXAxis}
                  onChange={toggleFlipXAxis}
                  style={{ marginRight: '5px' }}
                />
                Flip X-Axis
              </label>
            </div>
          </div>
        </div>
      )}
      
      <div 
        ref={graphContainerRef}
        style={styles.graphView}
        onMouseMove={handleMouseMove}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
      >
        <canvas
          ref={canvasRef}
          style={styles.canvas}
        />
        
        {!graphData && !accumulatedData && (
          <div style={styles.noData}>
            Draw a line on the camera feed to see intensity data
          </div>
        )}
        
        {/* Resize info overlay */}
        {showResizeInfo && (
          <div style={styles.resizeInfo}>
            Height: {graphSize.height}px
          </div>
        )}
        
        {/* Resize handle */}
        <div 
          style={styles.resizeHandle}
          onMouseDown={handleResizeStart}
          title="Resize graph height"
        >
          <svg width="12" height="12" viewBox="0 0 12 12">
            <path 
              d="M2,6 L10,6 M2,9 L10,9" 
              stroke="white" 
              strokeWidth="1.5"
              strokeLinecap="round"
            />
          </svg>
        </div>
      </div>
    </div>
  );
});

// Add display name for debugging
GraphComponent.displayName = 'GraphComponent';

export default GraphComponent;
